g2:c.PROCEDURE.g2:a.TEXT:
advance-signal()

begin
  case (the mode of TS) of
    stop: call change-signal("proceed");
    slow: call change-signal("stop");
    proceed: call change-signal("slow");
  end;
end

{ This returns a 2-d array of values.  If we only wanted a 1 dim array of data, we could just use a sequence(1,2,3).  }

G2ChartGenerator(max: quantity) = (structure)

Titles: sequence  = sequence("Set 1", "G.D.P.", "Profits", "R.O.I", "Stock Price", "Yield", "Level");
Set1: sequence = sequence(Titles[random(6)], random(max), random(max), random(max), random(max), random(max));
Set2: sequence = sequence(Titles[random(6)], random(max), random(max), random(max), random(max), random(max));
Set3: sequence = sequence(Titles[random(6)], random(max), random(max), random(max), random(max), random(max));

begin
  return structure(com-dimensions: sequence(6,3), com-lower-bounds: 1, com-elements: concatenate (Set1, Set2, Set3));
end

G2StringGenerator() = (text)

begin
    conclude that next-index = next-index + 1;
    if next-index >= 4 then conclude that next-index = 0;
    return stringarray[next-index];
end

G2RandomGenerator(max: quantity) = (value)

retval: quantity;

begin
    retval = random(max);
    return retval;
end

change-signal(mode: text)

begin
    case (mode) of 
    "stop" :
        begin
            change the green-region icon-color of TS to black;
            change the yellow-region icon-color of TS to black;
            change the red-region icon-color of TS to red;
            conclude that the mode of TS is stop;
        end;
    "slow" :
        begin
            change the green-region icon-color of TS to black;
            change the yellow-region icon-color of TS to yellow;
            change the red-region icon-color of TS to black;
            conclude that the mode of TS is slow;
        end;
    otherwise :
        begin
            change the green-region icon-color of TS to green;
            change the yellow-region icon-color of TS to black;
            change the red-region icon-color of TS to black;
            conclude that the mode of TS is proceed;
        end;
    end;

  call g2com-start-over-interface("CYCLELIGHTS", the mode of TS, the symbol vbdemo-interface);
end
