g2:c.PROCEDURE.g2:a.TEXT:
initialize-repository-objects()
    obj: class repository-object;
    attr-name: symbol;
    param: class parameter;
begin
    for obj = each repository-object do
        for attr-name = each symbol that is a user-defined attribute name of obj do
            if attr-name is not repository-tag then begin
                param = the parameter that is an attribute of obj named by attr-name;
                conclude that the attribute-name of param = attr-name; end;
            end;
        end;
    activate the subworkspace of repository-whenevers;
end

r-lock-by-name(r-class: symbol, r-name: symbol) = (symbol, integer)
    repository-tag: integer;
    status: symbol;
begin
    repository-tag = call r-tag-query(r-class, r-name) across repository-interface;
    if repository-tag < 0 then begin
        start log-message("Repository does not contain a [r-class] by the name of [r-name]");
        return the symbol failure, repository-tag; end;
    status = call r-lock-object(repository-tag) across repository-interface;
    case (status) of 
        timeout : begin 
            start log-message("Timed out trying to get a lock on object");
            return the symbol timeout, repository-tag; end;
        deleted : begin
            start log-message("Object has been deleted");
            return the symbol deleted, repository-tag; end;
        success : begin
            return the symbol success, repository-tag; end;
        end;
end

r-update-client-copy(handle-of-copy: integer, attribute-name: symbol, mod: class parameter)
    re-enable-mods: truth-value = false;
    local-copy: class object;
    copy-attr: class parameter;
    generic: class generic-r-object;
begin
    if repository-modifications-enabled then begin
        conclude that repository-modifications-enabled is false;
        re-enable-mods = true; end;
    local-copy = call g2-get-item-from-network-handle (handle-of-copy,repository-interface);
    copy-attr = the parameter that is an attribute of local-copy named by attribute-name;
    conclude that copy-attr = the value of mod;
    for generic =  each generic-r-object do  {this ugly but im in a hurry }
        if the name of local-copy = name-of-target then begin
            copy-attr = the parameter that is an attribute of generic named by attribute-name;
            conclude that copy-attr = the value of mod;
        end;
    end;
    allow other processing;
    if re-enable-mods then
        conclude that repository-modifications-enabled is true;
end

r-add-using-template(r-class: symbol, r-name: symbol)
    template-name: symbol;
    template: class repository-object;
    tag: integer;
begin
    template-name = symbol("generic-[r-class]");
    if the repository-object named by template-name exists then begin
        template = the repository-object named by template-name;
        change the name of template to r-name;
        tag = call r-add(template);
        change the name of template to template-name; end
    else
        start log-message("Could not find template [template-name] for class [r-class]");
end

r-add(new-object: class repository-object) = (integer)
    tag: integer;
begin
    tag = call r-add-object(new-object) across repository-interface;
    return tag;
end

r-modify(target: class repository-object,
            attribute-name : symbol,
            mod: value) = (symbol)
    status: symbol;
    tag: integer;
    packaged-mod: class object;
begin
    tag = the repository-tag of target;
    status = call r-lock-object(tag) across repository-interface;
    if status is not success then return status;
    if mod is an integer then
        status = call r-modify-object-integer(tag,attribute-name,mod) across repository-interface
    else if mod is a float then 
        status = call r-modify-object-float(tag,attribute-name,mod) across repository-interface
    else if mod is a truth-value then 
        status = call r-modify-object-truth-value(tag,attribute-name,mod) across repository-interface
    else if mod is a symbol then 
        status = call r-modify-object-symbol(tag,attribute-name,mod) across repository-interface
    else if mod is a text then 
        status = call r-modify-object-text(tag,attribute-name,mod) across repository-interface
    else
        status = the symbol failure;
    start r-unlock-object(tag) across repository-interface;
    return status;
end

r-watch-by-name(r-class: symbol, r-name: symbol) = (class repository-object, symbol)
    repository-tag: integer;
    status: symbol;
    new-view: class repository-object;
    new-view-handle: integer;
begin
    status, repository-tag = call r-lock-by-name(r-class, r-name);
    if status is not success then return the-void-repository-object, status;
    new-view = call r-snapshot(repository-tag) across repository-interface;
    conclude that the repository-tag of new-view = repository-tag;
    new-view-handle = call g2-register-on-network(new-view,repository-interface); 
    status = call r-watch-object(repository-tag,new-view-handle) across repository-interface;
    start r-unlock-object(repository-tag) across repository-interface; 
    return new-view, status;
end

r-snapshot-by-name(r-class: symbol, r-name: symbol) = (class repository-object, symbol)
    repository-tag: integer;
    status: symbol;
    snapshot: class repository-object;
begin
    repository-tag = call r-tag-query(r-class, r-name) across repository-interface;
    if repository-tag < 0 then begin
        start log-message("Repository does not contain a [r-class] by the name of [r-name]");
        return the-void-repository-object, the symbol failure; end;
    snapshot, status= call r-snapshot(repository-tag) across repository-interface; 
    conclude that the repository-tag of snapshot = repository-tag; 
    return snapshot, status;
end

update-generic-employee(employee-being-watched: class r-employee)
    re-enable-mods: truth-value = false;
begin
    if repository-modifications-enabled then begin
        conclude that repository-modifications-enabled is false;
        re-enable-mods = true; end;
    conclude that the social-security-number of generic-employee = the social-security-number of employee-being-watched;
    conclude that the title of generic-employee = the title of employee-being-watched;
    conclude that the supervisor of generic-employee = the supervisor of employee-being-watched;
    conclude that the pay-scale of generic-employee = the pay-scale of employee-being-watched;
    allow other processing;
    if re-enable-mods then
        conclude that repository-modifications-enabled is true;
end

add-employee(r-name: symbol)
    object-copy: class repository-object;
    status: symbol;
begin
    if repository-modifications-enabled then begin
        start log-message("Modifications must be disabled in order to add a new employee to the repository");
        return; end;
    start r-add-using-template(the symbol employee, r-name);
end

watch-employee(r-name: symbol)
    object-copy: class repository-object;
    status: symbol;
begin
    object-copy, status = call r-watch-by-name(the symbol r-employee, r-name);
    if status is success then begin
        call update-generic-employee (object-copy);
        transfer object-copy to the subworkspace of repository-interface at (640,repository-display-y-pos);
        conclude that repository-display-y-pos  = repository-display-y-pos - 100;
        conclude that repository-modifications-enabled is true; end;
end

log-message(new-message: text)
begin
  if message-board exists then show message-board with its bottom right corner at the bottom right corner of the screen;
  inform the operator for the next 5 seconds that 
          "[new-message]"; 
  change the size of message-board to minimum;
  show message-board with its bottom right corner at the bottom right corner of the screen; 
  wait for 5 seconds;
  hide message-board;
end

g2:c.METHOD.g2:a.TEXT:
disconnect(example: class gsi-example)
    interface: class gsi-interface;
    W, sub-W: class kb-workspace;
    member, sub-member: class item;
begin 
    W = the subworkspace of example; 
    interface = the gsi-interface upon W;
    for member = each item upon W do
        if the subworkspace of member exists then begin
            sub-W = the subworkspace of member;
            for sub-member = each item upon sub-W do
                if the subworkspace of sub-member exists then 
                    hide the subworkspace of sub-member; 
                end;
            hide sub-W;
            end;
        end;
    deactivate the subworkspace of interface;
    hide the subworkspace of interface;
    deactivate the subworkspace of example;
    hide the subworkspace of example;
    show startup with its top left corner at the top left corner of the screen;
end

launch(example: class gsi-example)
    interface: class gsi-interface;
begin
    if the gsi-interface upon the subworkspace of example does not exist then return;
    interface = the gsi-interface upon the subworkspace of example;
    if the gsi-interface-status of interface /= 2 then return;
    if the subworkspace of interface exists then 
    begin
        activate the subworkspace of interface;
        show the subworkspace of interface with its top left corner 300 units below the top left corner of the screen;
    end;
end

connect(example: class gsi-example)
    result : symbol;
begin
    result = call simple-dialog("Please launch [the corresponding-gsi-program-name of example] found in 
your GSI distribution directory.
No command line arguments required.");
    if result is cancelled then return;
    activate the subworkspace of example;
    show the subworkspace of example with its top left corner at the top left corner of the screen;
    hide startup;
end

g2:c.PROCEDURE.g2:a.TEXT:
send-updates (matrix: class float-array, 
                    updates: class integer-list)
    result: symbol;
    i, length : integer;
begin
    result = call update-remote-matrix (matrix,updates) across matrix-interface;
    start log-message("Remote array update was a [result]");
    length = the number of elements in updates;
    for i = 1 to length do
        remove the first integer from updates;  end;     
end

update-change-list(index: integer)
    el: integer;
begin
    for el = each integer in matrix-update-list do 
        if el = index then return; end;
    insert index at the beginning of matrix-update-list;
end

display-new-alarm(new-alarm: class alarm)
begin
   transfer new-alarm to the workspace of the item superior to this workspace at (alarm-x-pos, alarm-y-pos);
   conclude that alarm-x-pos = alarm-x-pos + 60;
    if alarm-x-pos > starting-x-pos + 500 then
    begin
        conclude that alarm-x-pos = starting-x-pos;
        conclude that alarm-y-pos = alarm-y-pos - 50;
    end;
end

write-file (filet:class file, int_quan:quantity, sym_quan:quantity,sx:quantity,ex:quantity,sy:quantity,ey:quantity,ss:symbol) = ()
   temp: quantity ;
begin
    if the status of filet = the symbol closed or the status of filet = the symbol g2 then
            start log-message("File has not been opened for writing yet. Choose OPEN-WRITE first.")
    else begin
        temp = call write (the handle of filet,int_quan,sym_quan,sx,ex,sy,ey,ss) across g2-to-gsi-rpc-interface;
        if ( temp < 0 )
           then begin
            conclude that the status of filet = the symbol closed;
           end;
    end;
end

copy-file (filer :class file, filew:class file) = ()
begin
    call open-file(filer, "r");
    call open-file(filew, "w"); 
   repeat 
    exit if the status of filer = the symbol eof or the status of filew = the symbol closed;
        call read-file(filer);
    exit if the status of filer = the symbol eof;
        call write-file(filew, 4, 1, the startx of filer, the endx of filer, the starty of filer, the endy of filer, the type of filer);
        wait for 1 second;
    end;
    call close-file (filer);
    call close-file (filew);
end

read-file (filet:class file) = ()
temp,sx,ex,sy,ey:quantity;
ss:symbol;
begin
if the status of filet = the symbol closed or the status of filet = the symbol g2 then
            start log-message("File has not been opened for reading yet. Choose OPEN-READ first.")
    else begin 
        temp, sx,ex,sy,ey,ss = call read(the handle of filet, 4, 1) across g2-to-gsi-rpc-interface;
        if ( temp < 0 )
           then begin
        conclude that the status of filet = the symbol eof;
       end
       else begin
        conclude that the startx of filet = sx;
        conclude that the endx of filet = ex;
        conclude that the starty of filet = sy;
        conclude that the endy of filet = ey;
        conclude that the type of filet = ss;
    end;
    end;
end

close-file (filet:class file) = ()
temp, fhandle :quantity; 
begin
    if the status of filet = the symbol g2 or the status of filet = the symbol closed then
          start log-message("Can't close file [the name of filet]. The file was not open.")
    else begin
temp = call close (the handle of filet) across g2-to-gsi-rpc-interface;
    if ( temp = 0 )
       then begin
        conclude that the status of filet = the symbol closed;
        end
    else
        begin
        conclude that the status of filet = the symbol open;
        end;
    conclude that the handle of filet = 0;
    conclude that the read-count of filet = 0;
    conclude that the write-count of filet = 0;
end;
end

open-file (filet:class file,mode:text) = ()
temp: quantity;
begin
    temp = call open (the fname of filet,mode) across g2-to-gsi-rpc-interface ;
    if ( temp > 0 )
       then begin
        conclude that the status of filet = the symbol open;
        end
    else
        begin
        conclude that the status of filet = the symbol closed;
        end;
    conclude that the handle of filet = temp;
    conclude that the read-count of filet = 0;
    conclude that the write-count of filet = 0;
end

reset-kb()
    W: class kb-workspace;
begin
    for W = each kb-workspace do
        hide W; end;
    show welcome at the center of the screen;
    reset knowledge-base;
end

simple-dialog(dialog-message: text) = (symbol)
    DW : class kb-workspace;
begin
    conclude that dialog-status is null;
    DW = the subworkspace of this procedure;
    change the text of the borderless-free-text upon DW to "[dialog-message]";
    show DW at the center of the screen;
    do in parallel until one completes 
        wait until dialog-status is cancelled checking every 0.1 seconds;
        wait until dialog-status is okayed checking every 0.1 seconds;
    end;
    hide DW;
    return dialog-status;
end
