g2:c.PROCEDURE.g2:a.TEXT:
g2-send-soap-request(URL: text, request: structure) = (structure)
web-request, web-response, response: structure;
body: text;
begin
  web-request = system call g2-make-web-request-from-soap-message(request);
  web-response = call g2-send-web-request(URL, web-request);
  call g2-check-http-response-status-code(web-response);
  body = call g2-read-http-entity-body(web-response);
  web-response = change-attribute(web-response, entity, body);
  response = system call g2-make-soap-message-from-web-response(web-response);
  return response;
end

g2-send-web-request(URL: text, request: structure) = (structure)
connection: class g2-socket;
start-line, header-line: text;
header-lines: sequence = sequence();
response: structure;
length: integer;
begin
  request = system call g2-format-web-request(URL, request);
  // The above system call raises a stack error if the URL scheme is not http, so everything below assumes this is an HTTP request.  This will need to be refactored if we ever support other URL schemes (e.g. ftp, news, mailto, file).
  connection = call g2-tcp-connect(the host of request, the port of request, structure());
  call g2-write-string(connection, the message of request);
  repeat
    start-line = call g2-read-line(connection);
    repeat
      exit if the g2-socket-status of connection = the symbol connection-closed;
      header-line = call g2-read-line(connection);
      exit if header-line = "";
      header-lines = insert-at-end(header-lines, header-line);
    end;
    response = system call g2-parse-http-response(start-line, header-lines);
    exit if the status-code of response >= 200;
    // RFC 2616, 10.1 Informational 1xx: "Unexpected 1xx status responses MAY be ignored by a user agent."
  end;
  length = the transfer-length of response;
  if length = 0 then
    call g2-tcp-close(connection) // TO DO: persistent connections
  else
    // Otherwise it's the caller's responsibility to close the connection.
    response = change-attribute(response, connection, connection);
  return response;
end

g2-shutdown-http-server(server: class http-server) = ()
listener: item-or-value;
invocation: class procedure-invocation;
begin
  if the http-server-port of server exists then begin
    listener, invocation = system call g2-shutdown-http-server(server);
    abort invocation;
    call g2-tcp-close(listener);
  end
end

g2-handle-http-request-as-soap(server: class http-server, request: structure, soap-handler: class procedure, user-data: item-or-value) = (structure)
soap-request, soap-response, response: structure;
begin
  soap-request = system call g2-make-soap-message-from-http-request(request);
  soap-response = call soap-handler(server, soap-request, user-data);
  response = system call g2-make-http-response-from-soap-message(soap-response);
  return response;
end

g2-invoke-web-service-operation(endpoint-reference: structure, operation-name: text, input-message: structure) = (structure)
invocation, request, response, output-message: structure;
body: text; 
begin
  invocation = system call g2-make-web-service-operation-invocation(endpoint-reference, operation-name, input-message);
  response = call the procedure named by the sys-proc-name of invocation(the URL of invocation, the request of invocation);
  if the transfer-length of response exists then begin
    // The transfer-length attribute means it's HTTP and we need to fill in the entity attribute.
    call g2-check-http-response-status-code(response);
    body = call g2-read-http-entity-body(response);
    response = change-attribute(response, entity, body);
  end;
  output-message = system call g2-get-message-from-web-service-operation-response(invocation, response);
  return output-message;
end

g2-import-web-service-description-from-xml-text(document: text) = (class web-service-description)
description: class web-service-description;
begin
  description = system call g2-import-web-service-description-from-xml-text(document);
  return description;
end

g2-http-listen(server: class http-server, port: integer) = ()
listener, connection: item-or-value;
begin
  if the http-server-port of server exists then
    call g2-shutdown-http-server(server);
  listener = call g2-tcp-listen(port, structure(exact: true));
  system call g2-http-listen(server, port, listener, this procedure-invocation);
  repeat
    connection = call g2-tcp-accept(listener);
    start g2-serve-http-connection(server, connection);
  end
end

g2-serve-http-connection(server: class http-server, connection: item-or-value) = ()
start-line, header-line: text;
header-lines: sequence = sequence();
request: structure;
length: integer;
body: text;
response: structure;
err: class error;
location: text;
transient-p: truth-value; 
raw-response: text;
chunk: sequence;
begin
  begin
    repeat 
      start-line = call g2-read-line(connection);
      // RFC 2616, 4.1 Message Types: "In the interest of robustness, servers SHOULD ignore any empty line(s) received where a Request-Line is expected. In other words, if the server is reading the protocol stream at the beginning of a message and receives a CRLF first, it should ignore the CRLF."
      exit if not (start-line = "");
    end;
    repeat
      header-line = call g2-read-line(connection);
      exit if header-line = "";
      header-lines = insert-at-end(header-lines, header-line);
    end;
    request = system call g2-parse-http-request(start-line, header-lines);
    length = the transfer-length of request;
    if length >= 0 then begin
      body = call g2-read-bytes-as-text(connection, length);
      request = change-attribute(request, entity, body);
    end;
    if the procedure named by the http-server-dispatch of server exists then
      response = call the procedure named by the http-server-dispatch of server(server, request)
    else
      response = structure(status-code: 404, headers: structure(Content-Type: "text/plain"), entity: "404 Not Found");
  end on error (err)
    location = "";
    if the error-source-item of err exists then
      location = "[the error-source-item of err], line [the error-source-line of err], column [the error-source-column of err]

";
    response = structure(status-code: 500, headers: structure(Content-Type: "text/plain"), entity: "500 Internal Server Error

[location][the error-description of err]");
    transient-p = call g2-system-predicate(err, the symbol transient);
    if transient-p then delete err;
  end;
  raw-response = system call g2-format-http-response(response);
  call g2-write-string(connection, raw-response);
  if the entity-producer of response exists then begin
    repeat
      chunk = call the entity-producer of response(response);
      exit if chunk = sequence();
      call g2-write-bytes(connection, chunk);
    end
  end on error (err)
    transient-p = call g2-system-predicate(err, the symbol transient);
    if transient-p then delete err;
  end;
  call g2-tcp-close(connection);
end

g2-read-http-entity-body(http-message: structure) = (text)
len: integer;
body: text = "";
chunk: text;
line: text;
e: class error;
begin
  if the Transfer-Encoding of the headers of http-message does not exist then begin
    len = the transfer-length of http-message;
    // TO DO: parse the entity body in chunks in case it's too big to fit in a text.
    if len > 0 then
      body = call g2-read-bytes-as-text(the connection of http-message, len)
    else if len < 0 then repeat
      exit if the g2-socket-status of the connection of http-message is connection-closed;
      chunk = call g2-read-block(the connection of http-message);
      body = "[body][chunk]";
    end;
    // If len = 0, there is no connection attribute, because it's already been closed.
    if len /= 0 then call g2-tcp-close(the connection of http-message); // TO DO: persistent connections
  end else case (the Transfer-Encoding of the headers of http-message) of
    // Transfer-codings are defined by RFC 2616 section 3.6:
    // http://www.w3.org/Protocols/rfc2616/rfc2616-sec3.html#sec3.6
    "chunked": begin
      repeat
        line = call g2-read-line(the connection of http-message);
        len = system call g2-parse-http-chunk-header(line);
        exit if len = 0;
        chunk = call g2-read-bytes-as-text(the connection of http-message, len);
        body = "[body][chunk]";
        // Discard the CRLF after the chunk.
        call g2-read-line(the connection of http-message);
      end;
      // Read and discard the trailer headers, terminated by a blank line.
      // TO DO: save the trailer headers
      repeat
        line = call g2-read-line(the connection of http-message);
        exit if line = "";
      end;
    end;
    // TO DO: "gzip", "compress", "deflate" transfer-codings
    // Note that the "identity" transfer-coding was removed by the official errata:
    // http://skrb.org/ietf/http_errata.html#identity
    otherwise: begin
      create an error e;
      conclude that the error-description of e = "Unknown Transfer-Encoding: [the Transfer-Encoding of the headers of http-message]";
      signal e;
    end;
  end;
  return body;
end

g2-import-web-service-description(URL: text) = (class web-service-description)
response: structure;
document: text;
description: class web-service-description;
begin
  response = call g2-send-web-request(URL, structure(headers:structure(Connection:"Keep-Alive")));
  call g2-check-http-response-status-code(response);
  document = call g2-read-http-entity-body(response);
  description = call g2-import-web-service-description-from-xml-text(document);
  return description;
end

g2-check-http-response-status-code(response: structure) = ()
e: class error;
begin
  if the status-code of response >= 400 then begin
    create an error e;
    conclude that the error-description of e = "[the status-code of response] [the reason-phrase of response]";
    if the transfer-length of response /= 0 then
      call g2-tcp-close(the connection of response);
    // TO DO: use a custom error class, include the response, let the handler close the connection
    signal e;
  end
end
