c.GRM-PROCEDURE.g2:a.TEXT:
release-set-title-blocks (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En: symbol;
Et, LogText: text;
Passed: truth-value = true;
TB: class uil-title-block; 

begin

LogText = "--- Setting Title Blocks -- [the current subsecond time as a time stamp]---[CRLF]";

call grm-set-title-blocks-info (the grm-util-pkg-name of release-build-data, the grm-long-version of release-build-data , the grm-copyright of release-build-data, Win);

for TB = each uil-title-block do
    LogText =  "[LogText]
    Module: [the uil-module-title of TB]
      Version: [the version of the icon-variables of TB]"; 
end; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-set-version-info-objects (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En: symbol;
Et, LogText: text;
LTRes: class gfr-local-text-resource;
Passed: truth-value = true;
VInfo: class gfr-version-information-object;

begin

LogText = "--- GFR Version Infos  (Pkg, Desc, Build#, Oldest#, Copyright, MinG2) -- [the current subsecond time as a time stamp] ---[CRLF]"; 

LogText = call grm-set-version-information (the grm-util-pkg-name of release-build-data, the grm-long-version of release-build-data, quantity (the grm-current-build of release-build-data), quantity (the grm-oldest-build of release-build-data), the grm-min-g2-version of release-build-data, the grm-copyright of release-build-data, "[the current time as a time stamp]", LogText);

for VInfo = each gfr-version-information-object do
    if the gfr-package-name of VInfo = the grm-util-pkg-name of release-build-data then 
        LogText = "[LogText][CRLF]   [the gfr-package-name of VInfo], [the gfr-version-description of VInfo],  [the gfr-version-number of VInfo], [the gfr-oldest-compatible-version of VInfo], [the gfr-copyright-information of VInfo], [the gfr-minimum-g2-version of VInfo]"; 
end;

LogText = "[LogText][CRLF][CRLF]       Local Text Resources  (Mod, ResGrp, GfrVer)";

for LTRes = each gfr-local-text-resource do
    conclude that the gfr-version of LTRes = the grm-long-version of release-build-data;
    LogText = "[LogText][crlf]    [the containing-module of LTRes]  [the gfr-resource-group of LTRes]  [the gfr-version of LTRes]  [the gfr-language of LTRes]";
end;

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win); 
    return false;
end

