g2:c.PROCEDURE.g2:a.TEXT:
release-get-items-in-ws-hierarchy (WS: class kb-workspace, ClassName: symbol) = (sequence)

ClassDef: class class-definition;
Itm: class item;
Seq: sequence = sequence ();
SubSeq: sequence = sequence ();
SWS: class kb-workspace;

begin

for Itm = each item upon WS do
    if the class-definition named by the class of Itm exists then begin
        ClassDef = the class-definition named by the class of Itm;
        if ClassName is a member of the class-inheritance-path of ClassDef then
            Seq = insert-at-end (Seq, Itm);
        if the subworkspace SWS of Itm exists then begin
            SubSeq = call release-get-items-in-ws-hierarchy (SWS, ClassName);
            if the number of elements in SubSeq > 0 then
            Seq = concatenate (Seq, SubSeq);
        end;
    end;
end;

return Seq;

end

c.GRM-PROCEDURE.g2:a.TEXT:
release-setup-sysmod-1 (Step: class grm-step, Win: class g2-window) = (truth-value)

ArgSigs: integer = 0;
BFT: class borderless-free-text;
CRLF: text = "
";
En: symbol;
Et, LogText: text;
Passed: truth-value;
Proc: class procedure;
Procs: sequence;
ProcNo: integer;
TSMarks: integer = 0;

begin

LogText = "--- Setting up sys-mod (Part 1) -- [the current subsecond time as a time stamp]  ---[CRLF]";
LogText = "[LogText][CRLF]    Updating System Procedures WS Version"; 

Passed = call release-update-sysproc-version ();
if Passed = false then begin
    LogText = "[LogText][CRLF]    Failed to update System Procedures WS Version";
    call grm-log-step (Step, LogText, Passed, Win);
    return Passed;
end;

LogText = "[LogText][CRLF]    Checking Strip Marks & Argument Signatures";
Procs = call release-get-items-in-ws-hierarchy (g2-system-procedures, the symbol PROCEDURE);

for Proc = each procedure in Procs do
    if the strip-text-mark of Proc = true then
        TSMarks = TSMarks + 1;
    if there exists a borderless-free-text BFT that is proc-arg-signature-for Proc then
        ArgSigs = ArgSigs + 1;
end;

ProcNo = the number of elements in Procs;
LogText = "[LogText][CRLF]        Total Procs: [ProcNo]  Strip Marks: [TSMarks]  Arg Sigs [ArgSigs]";

if TSMarks = ProcNo and ArgSigs = ProcNo then
    Passed = true
else
    Passed = false;

LogText = "[LogText][CRLF]    Updating g2-get-sys-mod-version";
Passed = call release-update-version-getter ();
if Passed = false then begin
    LogText = "[LogText][CRLF]    Failed to update g2-get-sys-mod-version";
    call grm-log-step (Step, LogText, Passed, Win);
    return Passed;
end; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

g2:c.PROCEDURE.g2:a.TEXT:
release-update-version-getter () = (truth-value)

En: symbol;
Et, NewText, Rev, RType, V1, V2: text;
Pos: integer;
S: structure;
ShortVer: text = the grm-short-version of release-build-data;
Txt: text = the text of g2-get-sys-mod-version;

begin

S = find-next-pattern ("{0-9}+", ShortVer, 1);
Pos = the end-index of S;
V1 = get-from-text (ShortVer, 1, Pos - 1);
V2 = get-from-text (ShortVer, Pos, Pos); 
RType = get-from-text (ShortVer, Pos + 1, Pos + 1);
case (RType) of
    "a":  RType = "ALPHA";
    "b":  RType = "BETA";
    "p":  RType = "PROTOTYPE";
    "r":  RType = "RELEASE";
end;

Rev = get-from-text (ShortVer, Pos + 2, length-of-text (ShortVer));
NewText = "return [V1].[V2], [Rev], the symbol [RType]";

S = find-next-pattern ("return {0-9}+.{0-9}, {0-9}+, the symbol {A-Z}*{a-z}*", Txt, 1);

Txt = replace-in-text (NewText, Txt, the start-index of S, the end-index of S);
change the text of g2-get-sys-mod-version to Txt;
make g2-get-sys-mod-version permanent; 

return true;

end

on error (En, Et)
    return false;
end

release-update-sysproc-version () = (truth-value)

En: symbol;
Et, NewText: text;
Txt: text = the text of sys-mod-title-block;
S: structure;

begin

S = find-next-pattern ("Version {0-9}+.{0-9}( {A-Z}+{a-z}* ?{A-Z}*{a-z}*.? ?{0-9}+)?", Txt, 1);
NewText = "Version [the grm-long-version of release-build-data]";
Txt = replace-in-text (NewText, Txt, the start-index of S, the end-index of S);
change the text of sys-mod-title-block to Txt;
make sys-mod-title-block permanent;

return true;

end

on error (En, Et)
    return false;
end

c.GRM-PROCEDURE.g2:a.TEXT:
release-recompile-procs (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En: symbol;
Et, LogText: text;
Passed: truth-value = true;
UData: class integer-parameter = release-inspect-integer;

begin

LogText = "--- Recompiling procedures -- [the current subsecond time as a time stamp] ---[CRLF]";
call permit-system-call-grammar ();
LogText = "[LogText][CRLF]    Recompiling GFR procedures";
conclude that Udata = -1;
call g2-start-inspect-session ("recompile every procedure assigned to module gfr", release-inspect-callback, UData, 1, 1.0);
wait until (UData >= 0) checking every 1 second;
LogText = "[LogText][CRLF]    recompiled [UData] procedures in GFR";

LogText = "[LogText][CRLF]    Recompiling GXL procedures";
conclude that Udata = -1; 
call g2-start-inspect-session ("recompile every procedure assigned to module gxl", release-inspect-callback, UData, 1, 1.0 );
wait until (UData >= 0) checking every 1 second;

conclude that Udata = -1; 
call g2-start-inspect-session ("recompile every procedure whose status is incomplete and assigned to module gxl", release-inspect-callback, UData, 1, 1.0 );
wait until (UData >= 0) checking every 1 second; 
LogText = "[LogText][CRLF]    recompiled [UData] procedures in GXL"; 

LogText = "[LogText][CRLF]    Recompiling GMS procedures";
conclude that Udata = -1; 
call g2-start-inspect-session ("recompile every procedure assigned to module gms", release-inspect-callback, UData, 1, 1.0 );
wait until (UData >= 0) checking every 1 second; 
LogText = "[LogText][CRLF]    recompiled [UData] procedures in GMS"; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

