release-uninitialize-gold-books (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
Array: class g2-array; 
Book: class gold-book;
En: symbol; 
Et, LogText: text;
Passed: truth-value = true;
Table: class _gold-table; 

begin

LogText = "--- Uninitializing Gold Books -- [the current subsecond time as a time stamp] ---[CRLF]";

for Book = each gold-book do
    conclude that the _gold-initialized-p of Book = false;
    for Table = each _gold-table that is an attribute of Book do
        for Array = each g2-array that is an attribute of Table do
            change the array-length of Array to 0;
            call gfr-array-copy-elements-to-initial-values (Array);
        end;
    end; 
    make Book permanent; 
    LogText = "[LogText][CRLF]        Calling gold-uninitialize for: [the name of Book]";
end;

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-delete-protools (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En: symbol;
Et, LogText: text;
ExpNotes: integer = 17;
ModInfo: class module-information;
Mods: sequence;
N, N1, N2: integer;
Passed: truth-value = true;
UData: class integer-parameter = release-inspect-integer;

begin

LogText = "--- Deleting Protools & Ptroot -- [the current subsecond time as a time stamp] ---[CRLF]";

{*** Get rid of Protools & Ptroot ***}
call g2-delete-module (the symbol PROTOOLS, true);
call g2-delete-module (the symbol PTROOT, true);
allow other processing;

N1 = the count of each module-information ModInfo such that (the text of the notes of ModInfo /= "OK");

for ModInfo = each module-information do
    Mods = the directly-required-modules of ModInfo;
    for N = 0 to the number of elements in Mods - 1 do
        if Mods [N] = the symbol PROTOOLS or Mods [N] = the symbol PTROOT then begin
            Mods = remove (Mods, N);
            conclude that the directly-required-modules of ModInfo = Mods;
        end;
    end;
end;

N2 = the count of each module-information ModInfo such that (the text of the notes of ModInfo /= "OK"); 
LogText = "[LogText][CRLF]    Removing Protools";
LogText = "[LogText][CRLF]    Module Information Objects with Notes -- Before Cleanup: [N1]  After Cleanup: [N2]";

{*** Show items with notes ***}
conclude that Udata = -1; 
call g2-start-inspect-session ("show on a workspace every item with notes", release-inspect-callback, UData, 1, 1.0);
wait until (UData >= 0) checking every 1 second;
LogText = "[LogText][crlf]        Items with Notes, ([ExpNotes] expected):  [UData]";

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

