release-package-prep (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En: symbol;
Et, LogText: text;
Passed: truth-value = true;

begin

LogText = "--- Doing Package Prep -- [the current subsecond time as a time stamp] ---[CRLF]";

{*** Do the package prep stuff ***}
call g2-enter-package-preparation-mode ();
call g2-flush-change-log-for-entire-kb ();
call g2-strip-texts-now ();
Passed, LogText = call grm-test-text-stripping (LogText, Win);
if not (Passed) then begin
    LogText = "[LogText][CRLF]    Failed test strip test";
    call grm-log-step (Step, LogText, Passed, Win); 
    return Passed;
end;
call g2-make-workspaces-proprietary-now ();
call g2-leave-package-preparation-mode (); 

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

release-setup-sysmod-2 (Step: class grm-step, Win: class g2-window) = (truth-value)

CRLF: text = "
";
En: symbol;
Et, LogText: text;
Passed: truth-value = true;
SysmodBldInst: symbol = the symbol g2-sys-mod-build-instructions;
WS: class kb-workspace;

begin

LogText = "--- Setting up sys-mod (Part 2) -- [the current subsecond time as a time stamp] ---[CRLF]";

LogText = "[LogText][CRLF]    Deleting the sys-mod build instructions WS";
if the kb-workspace WS named by SysmodBldInst exists then begin
    Make WS transient;
    delete WS;
end;

LogText = "[LogText][CRLF]    Hiding g2-system-procedures WS";
hide g2-system-procedures;

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end

