release-create-dist-and-dev-directories (Step: class grm-step, Win: class g2-window) = 

(truth-value)

BuildData: class grm-data-object = release-build-data;
CRLF: text = "
";
CycleCode: text = the grm-cycle-code of release-build-data; 
En: symbol;
DevDir, DistDir, SrcDir, Et, LogText: text;
DevDirExists: truth-value = true;
DistDirExists: truth-value = true;
SrcDirExists: truth-value = true;
Passed: truth-value = true;
ShortVer: text = the grm-short-version of release-build-data;
Separator: text = "/";

begin
call grm-open-log ("Release-Gxl", Win);
LogText = "--- Creating DEV & DIST directories -- [the current subsecond time as a time stamp]---[CRLF]";

if (is-contained-in-text("\", the grm-source-path of BuildData)) then begin
  Separator = "\";
end;

SrcDir = "[the grm-source-path of BuildData][Separator]kbs[Separator]sources";
SrcDirExists = call g2-directory-exists (SrcDir);
DevDir = "[the grm-utils-path of BuildData][Separator]dev[Separator][ShortVer]-[CycleCode]";        
DevDirExists = call g2-directory-exists (DevDir); 
DistDir = "[the grm-utils-path of BuildData][Separator]dist[Separator][ShortVer]-[CycleCode]";
DistDirExists = call g2-directory-exists (DistDir);

if not (DevDirExists) then begin
    call g2-spawn-process-to-run-command-line ("mkdir [DevDir]");
    LogText = "[LogText][CRLF]    Creating Dev Directory: [DevDir][CRLF]";
end;         
if not (DistDirExists) then begin
    call g2-spawn-process-to-run-command-line ("mkdir [DistDir]");
    LogText = "[LogText][CRLF]    Creating Dist Directory: [DistDir][CRLF]";
end;                
wait for 5 seconds;

{--- Now they should both exist ---}
DevDirExists = call g2-directory-exists (DevDir); 
DistDirExists = call g2-directory-exists (DistDir); 
if not (DevDirExists and DistDirExists) then begin
    LogText = "[LogText]    Missing [DistDir] and/or [DevDir]";
    Passed = false;
end else
    LogText = "[LogText]    Verified [DistDir] & [DevDir]"; 

call grm-log-step (Step, LogText, Passed, Win);
return Passed;

end

on error (En, Et)
    call grm-signal-error (Step, En, Et, Win);
    return false;
end
