g2:c.PROCEDURE.g2:a.TEXT:
CLASS-ATT-DESCRIPTION-TEST(node: class benchmark-test) 
att-desc: sequence;
in-str: structure;
vlrtmp1: text = "value2";
begin
    {Scenario 1: retrieving attributes for system defined classes (2 classes: dial and digital-clock) }
    att-desc = call g2-get-attribute-descriptions-of-class (the symbol dial, sequence(the symbol expression-to-display, the symbol display-update-interval));
    if the number of elements in att-desc /= 2 then begin
        call fail(node, "Trying to retrieve descriptions for the attributes expression-to-display and display-update-interval from the dial class. Two structures (one for each attribute) were expected, but received [att-desc]");
        return;
    end;
    in-str = att-desc[0];
    if (the text-readable of in-str /= true) or (the text-writable of in-str /= true) then begin
        call fail(node, "Text readable and text writable for the class dial should be set to true. But they were set to: text-readable = [the text-readable of in-str], text-writable = [the text-writable of in-str]");
        return;
    end;

    att-desc = call g2-get-attribute-descriptions-of-class (the symbol digital-clock , sequence(the symbol readout-table-display-value));
    if the number of elements in att-desc /= 1 then begin
        call fail(node, "Trying to retrieve descriptions for the attribute expression-to-display from the digital-clock class. It was expected one structure describing the attribute, but it was returned [att-desc]");
        return;
    end;
    in-str = att-desc[0];
    if (the text-readable of in-str /= true) then begin
        call fail(node, "Text readable of digital-clock should have been set to true, but it was set to [the text-readable of in-str]");
        return;
    end;


    {Scenario 2: retrieving attributes for user defined classes (2 classes: my-class-dial and my-class-meter) } 

    att-desc = call g2-get-attribute-descriptions-of-class (the symbol my-class-dial, sequence(the symbol my-att-dial));
    if the number of elements in att-desc /= 1 then begin
        call fail(node, "Trying to retrieve descriptions for the customized attribute from the my-class-dial class. Expected number of elements 1 but received [att-desc]");
        return;
    end;
    in-str = att-desc[0];
    if (the text-readable of in-str /= true) then begin
        call fail(node, "Text readable of my-att-dial must be set to true, but it is set to [the text-readable of in-str]");
        return;
    end;

    att-desc = call g2-get-attribute-descriptions-of-class (the symbol my-class-meter , sequence(the symbol spec1, the symbol spec2)); 
    if the number of elements in att-desc /= 2 then begin
        call fail(node, "Trying to retrieve descriptions for some customized attributes (spec1 and spec2) from the my-class-meter class. Expected 2 elements but received [att-desc]");
        return;
    end;
    in-str = att-desc[0];
    if (the text-readable of att-desc[0] /= true) and (the text-readable of att-desc[1] /= true ) then begin
        call fail(node, "Text readable of spec1 and spec2 must be set to true. But the result was: text-readable of spec1 = [the text-readable of att-desc[0]],  text-readable of spec2 = [the text-readable of att-desc[1]]");
        return;
    end;

    {retrieving a complex attribute}
    att-desc = call g2-get-attribute-descriptions-of-class (the symbol my-class-meter , sequence(the symbol spec3)); 
    if the number of elements in att-desc /= 1 then begin
        call fail(node, "Trying to retrieve descriptions for a complex customized attribute from the my-class-meter class. Expected 1 element but received [att-desc]");
        return;        
    end;
    in-str = att-desc[0];
    att-desc = the initial-value of in-str;
    if the value2 of att-desc[0] /= vlrtmp1 then begin
        call fail(node, "The value for the value2 field of the first structure of the sequence attribute was not expected. Expected [vlrtmp1] returned [the value2 of att-desc[0]]");
        return;
    end; 

    {Scenario 3: using the value true to return descriptions for every visible attribute in a class}
    att-desc = call g2-get-attribute-descriptions-of-class (the symbol my-class-meter , true);
    if the number of elements in att-desc = 0 then begin
        call fail(node, "Trying to retrieve descriptions for every visible attribute in a class. Expected more than 0 received [att-desc]");
        return;        
    end;    


    call pass (node);
end

ATT-VISIBLE-MODE-TEST(node: class benchmark-test) 
qtt-system: sequence = sequence();
qtt-smbl: sequence = sequence(); 
qtt-smbl-rest: sequence = sequence(); 
found : truth-value = false;

admin-user-mode: integer = 0;
dev-user-mode: integer = 1;
enduser-user-mode: integer = 2;
begin
    {Scenario 1: comparing the visible attributes for system item, user defined item and user defined item with restrictioning configurations}

    qtt-system = call qtt-attributes-helper(the symbol symbol-list);
    qtt-smbl = call qtt-attributes-helper (my-symbol-list);
    qtt-smbl-rest = call qtt-attributes-helper (my-restricted-symbol-list);

    {comparing the item symbol-list against my-symbol-list instance. Only the administrator have access to the attribute ITEM-CONFIGURATION on the my-symbol-list instance}
    if (qtt-system[admin-user-mode ] /= qtt-smbl[admin-user-mode ]) or ((qtt-system[dev-user-mode] - 1)/= qtt-smbl[dev-user-mode ]) or ((qtt-system[enduser-user-mode ] - 1)/= qtt-smbl[enduser-user-mode ]) then begin
        call fail(node, "The user modes developer and end-user should have no access to the attribute ITEM-CONFIGURATION when it comes to a symbol-list instance: 
                my-symbol-list user-mode administration: should have [qtt-system[admin-user-mode]] got [qtt-smbl[admin-user-mode]]
                my-symbol-list user-mode developer: should have [qtt-system[dev-user-mode] - 1] got [qtt-smbl[dev-user-mode]]
                my-symbol-list user-mode end-user: should have [qtt-system[enduser-user-mode] - 1] got [qtt-smbl[enduser-user-mode]]");
        return;
    end;


    {Comparing the item my-symbol-list instance against my-restricted-symbol-list, the end-user should not have access to the attribute ELEMENT-TYPE which was restricted on its item-configuration field}
    if (qtt-smbl[admin-user-mode] /= qtt-smbl-rest[admin-user-mode]) or (qtt-smbl[dev-user-mode] /= qtt-smbl[dev-user-mode]) or ((qtt-smbl[enduser-user-mode] - 1)/= qtt-smbl-rest[enduser-user-mode]) then begin
        call fail(node, "The user mode end-user for my-restricted-symbol-list should have no access to the attribute ELEMENT-TYPE. The quantity of accessed attributes on 
                my-restricted-symbol-list user mode administrator should have [qtt-smbl[admin-user-mode]] got [qtt-smbl-rest[admin-user-mode]] 
                my-restricted-symbol-list user mode developer should have [qtt-smbl[dev-user-mode]] got [qtt-smbl[dev-user-mode]] 
                my-restricted-symbol-list user mode end-user should have [qtt-smbl[enduser-user-mode] - 1] got [qtt-smbl-rest[enduser-user-mode]]");
        return;
    end;

    qtt-system = sequence();
    qtt-smbl = sequence();
    qtt-smbl-rest = sequence();

    {Scenario 2: comparing the attributes visible for system item dial, user defined instance (my-simple-dial-instance) of a user defined class (my-simple-class-dial) and user defined instance (my-restricted-dial-instance) of a user defined class (my-restricted-class-dial) with one more user defined attribute and restrictioning configurations}
    qtt-system = call qtt-attributes-helper (the symbol dial);
    qtt-smbl = call qtt-attributes-helper (my-simple-dial-instance);
    qtt-smbl-rest = call qtt-attributes-helper (my-restricted-dial-instance);

    {comparing the item dial against my-simple-dial-instance instance. Only the administrator have access to the attribute ITEM-CONFIGURATION on the my-simple-dial-instance instance}
    if (qtt-system[admin-user-mode] /= qtt-smbl[admin-user-mode]) or ((qtt-system[dev-user-mode] - 1)/= qtt-smbl[dev-user-mode]) or ((qtt-system[enduser-user-mode] - 1)/= qtt-smbl[enduser-user-mode]) then begin
        call fail(node, "The user modes developer and end-user should have no access to the attribute ITEM-CONFIGURATION of my-simple-dial-instance. 
            my-simple-dial-instance user mode administrator should have [qtt-system[admin-user-mode]] got [qtt-smbl[admin-user-mode]] 
            my-simple-dial-instance user mode developr should have [qtt-system[dev-user-mode] - 1] got [qtt-smbl[dev-user-mode]] 
            my-simple-dial-instance user mode end-user should have [qtt-system[enduser-user-mode] - 1] got [qtt-smbl[enduser-user-mode]]");
        return;
    end; 

    {Comparing the item instance my-simple-dial-instance against my-restricted-dial-instance: 
        1. my-restricted-dial-instance has one more attribute which was added on its class definition on class-specific-attributes: my-att-dial;
        2. the class specific attribute my-att-dial is restricted for the end-user mode
        This way, my-simple-dial-instance has to have less one attribute visible for all user modes with the exception of the end-user, which has to have the same}
    if (qtt-smbl[admin-user-mode]+2 /= qtt-smbl-rest[admin-user-mode]) or (qtt-smbl[dev-user-mode] + 2 /= qtt-smbl-rest[dev-user-mode]) or (qtt-smbl[enduser-user-mode] /= qtt-smbl-rest[enduser-user-mode]) then begin
        call fail(node, "my-simple-dial-instance should have two attributes less than my-restricted-dial-instance with the exception of the user mode end-user which had to have the same number because of the restriction imposing that this role cannot see the attributes my-att-dial and my-config-dial. Values for my-simple-dial-instance: user mode admin: [qtt-smbl[admin-user-mode]], developer: [qtt-smbl[dev-user-mode]], end-user: [qtt-smbl[enduser-user-mode]]. Values for my-restricted-dial-instance: administrator: [qtt-smbl-rest[admin-user-mode]], developer: [qtt-smbl-rest[dev-user-mode]], end-ser: [qtt-smbl-rest[enduser-user-mode]]");
        return;
    end;


    {Scenario 3: checking if the attribute my-att-dial  of  my-restricted-dial-instance is not visible for the user-mode end-user, but the same attribute is visible for the user-mode developer}
    qtt-system = call g2-get-attributes-visible-in-mode (my-restricted-dial-instance , the symbol end-user); 

    {the attribute my-att-dial is not visible for user-mode end-user for the object my-restricted-dial-instance}
    found = call att-present-helper (qtt-system, the symbol my-att-dial);
    if found = true then begin
        call fail(node, "The attribute my-att-dial wasn't suppose to be visible to the user-mode end-user for the object my-restricted-dial-instance");
        return;
    end;

    qtt-system = call g2-get-attributes-visible-in-mode (my-restricted-dial-instance , the symbol developer); 
    {the attribute my-att-dial is visible for user-mode developer for the object my-restricted-dial-instance}
    found = call att-present-helper (qtt-system, the symbol my-att-dial);
    if found /= true then begin
        call fail(node, "The attribute my-att-dial was supposed to be visible to the user-mode developer for the object my-restricted-dial-instance");
        return;
    end;

    call pass (node);

end

QTT-ATTRIBUTES-HELPER(the-item: item-or-value) = (sequence) 
found-attributes: sequence; 
user-modes: sequence = sequence(the symbol administrator, the symbol developer, the symbol end-user); 
qtt-system: sequence = sequence(); 
smbl: symbol; 
qtt: integer; 

begin
    for smbl = each value in user-modes do
        found-attributes = call g2-get-attributes-visible-in-mode (the-item, smbl);
        qtt = the number of elements in found-attributes;
        qtt-system = insert-at-end(qtt-system, qtt); 
    end; 
    return qtt-system;
end

ATT-PRESENT-HELPER(attributes: sequence, att-name: symbol) =(truth-value)
smbl:symbol;

begin
    for smbl = each value in attributes do
        if smbl = att-name then 
            return true;
    end;   
    return false;
end

ATT-TEXT-ITEM-TEST(node: class benchmark-test) 
att-texts: structure;
err-occured: truth-value = false;
err: class error;
qtt: integer = 0;
item-nm-dial: class dial;
txt-att1: text = "dialing";
txt-att2: text = "disk to the office";

txt-tmp-att1: text = "";
txt-tmp-att2: text = "config 1";

txt1: text = "MY-SYMBOL-LIST";
txt2: text = "0";
txt-tmp1: text;
txt-tmp2: text;

begin
    create a dial item-nm-dial;
    conclude that the my-att-dial of my-restricted-dial-instance = txt-tmp-att1;
    conclude that the my-config-dial  of my-restricted-dial-instance  = txt-tmp-att2;

    {  Scenario 1: testing for error cases. Calling g2-get-attribute-texts-of-item on:
          a) Non-existent item (an error is raised)
          b) Non-existent attribute of an existent item (returns empty structure) }

    begin
        att-texts = call g2-get-attribute-texts-of-item (my-nonexistent-item, true);
    end on error(err)
        delete err;
        err-occured = true;
    end; 

    if err-occured /= true then begin
        call fail(node, " g2-get-attribute-texts-of-item  was called on a non-existent item. An error was supposed to be raised and it was not.");
        return;
    end;
    att-texts = call g2-get-attribute-texts-of-item (my-symbol-list, sequence(the symbol nonex-att));
    qtt = call qtd-struct-names(att-texts);
    if qtt /= 0 then begin
        call fail(node, " g2-get-attribute-texts-of-item was called on an existent item but non-existent attribute. No text should be returned but returned [qtt] text(s)");
        return;
    end; 

    {Scenario 2: retrieving attributes for an item. In this case my-symbol-list
        a) one single attribute
        b) two attributes
        c) all attributes }
    att-texts = call g2-get-attribute-texts-of-item (my-symbol-list, sequence(the symbol names));    
    qtt = call qtd-struct-names(att-texts); 
    if qtt /= 1 then begin
        call fail(node, "Calling g2-get-attribute-texts-of-item for 1 attribute: expected 1 text, returned [qtt]");
        return;
    end;

    txt-tmp1 = the value of the names of att-texts;

    if (txt-tmp1 /= txt1) then begin
        call fail(node, "Scenario 2 - a:  value expected  [txt1]  value returned [txt-tmp1]");
        return;
    end; 


    att-texts = call g2-get-attribute-texts-of-item (my-symbol-list, sequence(the symbol names, the symbol list-size)); 
    qtt = call qtd-struct-names(att-texts); 
    if qtt /= 2 then begin
        call fail(node, "Calling g2-get-attribute-texts-of-item for 2 attributes: expected 2 texts, returned [qtt]");
        return;
    end; 
    txt-tmp1 = the value of the names of att-texts;
    txt-tmp2 = the value of the list-size of att-texts;
    if (txt-tmp1 /= txt1) or (txt-tmp2 /= txt2) then begin
        call fail(node, "Scenario 2 - b - Two values were requested: 1) value expected  [txt1]  value returned [txt-tmp1]; 2) value expected [txt2] value returned [txt-tmp2]");
        return;
    end; 

    att-texts = call g2-get-attribute-texts-of-item (my-symbol-list, true);    
    qtt = call qtd-struct-names(att-texts); 
    if qtt = 0 then begin
        call fail(node, "Calling g2-get-attribute-texts-of-item for all attributes: expected more than 0 texts, returned [qtt]");
        return;
    end; 

    {Scenario 3: retrieving attributes for a system defined item. In this case, a dial}
    att-texts = call g2-get-attribute-texts-of-item (item-nm-dial , true);
    qtt = call qtd-struct-names(att-texts); 
    if qtt = 0 then begin
        call fail(node, "Calling g2-get-attribute-texts-of-item for all attributes of a system item: expected more than 0 texts, returned [qtt]");
        return;
    end; 

    {Scenario 4: retrieving texts of user defined attributes for a user defined item. In this case, attributes my-att-dial and my-config-dial for my-restricted-dial-instance}  
    att-texts = call g2-get-attribute-texts-of-item (my-restricted-dial-instance , sequence(the symbol my-att-dial, the symbol my-config-dial));
    qtt = call qtd-struct-names(att-texts); 
    if qtt /= 2 then begin
        call fail(node, "Calling g2-get-attribute-texts-of-item for the two user defined attributes (my-att-dial and my-config-dial) of a user defined item (my-restricted-dial-instance): expected 2 texts, returned [qtt]");
        return;
    end; 

    txt-tmp-att1 = the my-att-dial of my-restricted-dial-instance;
    txt-tmp-att2 = the my-config-dial of my-restricted-dial-instance; 

    {Scenario 5: changing text for the attributes my-att-dial and my-config-dial for my-restricted-dial-instance and checking if the changes took effect}
    conclude that the my-att-dial of my-restricted-dial-instance = txt-att1;
    conclude that the my-config-dial  of my-restricted-dial-instance  = txt-att2;
    att-texts = call g2-get-attribute-texts-of-item (my-restricted-dial-instance , sequence(the symbol my-att-dial, the symbol my-config-dial));
    if (the value of the my-att-dial of att-texts /= txt-att1) and  (the value of the my-att-dial of att-texts = txt-tmp-att1) then begin
        call fail(node, "The text for the attribute my-att-dial is not as expected: expected [txt-att1] actual [the value of the my-att-dial of att-texts]");
        return;
    end;

    if (the value of the my-config-dial of att-texts /= txt-att2) and  (the value of the my-config-dial of att-texts = txt-tmp-att2) then begin
        call fail(node, "The text for the attribute my-config-dial is not as expected: expected [txt-att2] actual [the value of the my-config-dial of att-texts]");
        return;
    end; 


    call pass(node);
end

qtd-struct-names(str : structure) = (integer)
qtt : integer = 0;
sel: symbol;
begin
    for sel = each symbol that is an attribute name of str do
        qtt = qtt + 1;
    end;
    return qtt;
end

ATT-VALUES-ITEM-TEST(node: class benchmark-test) 
att-values: structure; 
att-texts: structure;
err-occured: truth-value = false;
err: class error;
qtt: integer = 0;
item-nm-dial: class dial; 
txt-att1: text = "dialing";
txt-att2: text = "disk to the office"; 
vl-att3: sequence = sequence (structure (value1: "different value1",
    value2: "different value2"),
  structure (value1: "another value1",
    value2: "another value2")); 

txt-tmp-att1: text = "";
txt-tmp-att2: text = "config 1"; 
vl-tmp-att3: sequence = sequence (structure (value1: "value1",
    value2: "value2"),
  structure (value1: "another value1",
    value2: "another value2"));

spec-tmp: sequence;

txt1: truth-value = true;
txt2: symbol = the symbol OK;
txt-tmp1: item-or-value ;
txt-tmp2: item-or-value ; 

begin
    create a dial item-nm-dial;   
    conclude that the my-att-dial of my-restricted-dial-item-instance = txt-tmp-att1;
    conclude that the my-config-dial  of my-restricted-dial-item-instance = txt-tmp-att2; 
    conclude that the my-specification of my-restricted-dial-item-instance = vl-tmp-att3;

    {  Scenario 1: testing for error cases. Calling g2-get-attribute-values-of-item on:
          a) Non-existent item (an error is raised)
          b) Non-existent attribute of an existent item (returns empty structure) }
    begin
        att-values = call g2-get-attribute-values-of-item (my-nonexistent-item, true);
    end on error(err)
        delete err;
        err-occured = true;
    end;
    if err-occured /= true then begin
        call fail(node, "g2-get-attribute-values-of-item was called on a non-existent item. An error was supposed to be raised and it was not.");
        return;
    end;
    att-values = call g2-get-attribute-values-of-item (my-symbol-list, sequence(the symbol nonex-att));
    qtt = call qtd-struct-names(att-values);
    if qtt /= 0 then begin
        call fail(node, " g2-get-attribute-values-of-item was called on an existent item but non-existent attribute. No value should be returned but returned [qtt] value(s)");
        return;
    end; 

    {Scenario 2: retrieving attributes for an item. In this case my-symbol-list
        a) one single attribute
        b) two attributes
        c) all attributes }
    att-values = call g2-get-attribute-values-of-item (my-symbol-list, sequence(the symbol item-active));    
    qtt = call qtd-struct-names(att-values); 
    if qtt /= 1 then begin
        call fail(node, "Calling g2-get-attribute-values-of-item for 1 attribute: expected 1 value, returned [qtt]");
        return;
    end;
    txt-tmp1 = the value of the item-active of att-values; 
    if txt-tmp1 /= txt1 then begin
        call fail(node, "Scenario 2 - a:  value expected  [txt1]  value returned [txt-tmp1]"); 
        return;
    end;

    att-values = call g2-get-attribute-values-of-item (my-symbol-list, sequence(the symbol item-active, the symbol item-status));    
    qtt = call qtd-struct-names(att-values); 
    if qtt /= 2 then begin
        call fail(node, "Calling g2-get-attribute-values-of-item for 2 attributes: expected 2 values, returned [qtt]");
        return;
    end; 
    txt-tmp1 = the value of the item-active of att-values; 
    txt-tmp2 = the value of the item-status of att-values;
    if (txt-tmp1 /= txt1) or (txt-tmp2 /= txt2) then begin
        call fail(node, "Scenario 2 - b - Two values were requested: 1) value expected  [txt1]  value returned [txt-tmp1]; 2) value expected [txt2] value returned [txt-tmp2]");
        return;
    end; 

    att-values = call g2-get-attribute-values-of-item (my-symbol-list, true);    
    qtt = call qtd-struct-names(att-values); 
    if qtt = 0 then begin
        call fail(node, "Calling g2-get-attribute-values-of-item for all attributes: expected more than 0 values, returned [qtt]");
        return;
    end; 

    {Scenario 3: retrieving attributes for a system defined item. In this case, a dial}
    att-values = call g2-get-attribute-values-of-item (item-nm-dial , true);
    qtt = call qtd-struct-names(att-values); 
    if qtt = 0 then begin
        call fail(node, "Calling g2-get-attribute-values-of-item for all attributes of a system item: expected more than 0 values, returned [qtt]");
        return;
    end; 

    {Scenario 4: retrieving values of user defined attributes for a user defined item. In this case, attributes my-att-dial, my-config-dial and  my-specification for MY-RESTRICTED-DIAL-ITEM-INSTANCE }  
    att-values = call g2-get-attribute-values-of-item (my-restricted-dial-item-instance , sequence(the symbol my-att-dial, the symbol my-config-dial, the symbol my-specification));
    qtt = call qtd-struct-names(att-values); 
    if qtt /= 3 then begin
        call fail(node, "Calling g2-get-attribute-values-of-item for the three user defined attributes (my-att-dial, my-config-dial and my-specification) of a user defined item (my-restricted-dial-item-instance): expected 3 values, returned [qtt]");
        return;
    end; 

    txt-tmp-att1 = the my-att-dial of my-restricted-dial-item-instance;
    txt-tmp-att2 = the my-config-dial of my-restricted-dial-item-instance; 


    {Scenario 5: changing value for the attributes my-att-dial and my-config-dial for my-restricted-dial-item-instance and checking if the changes took effect}
    conclude that the my-att-dial of my-restricted-dial-item-instance = txt-att1;
    conclude that the my-config-dial  of my-restricted-dial-item-instance  = txt-att2;
    conclude that the my-specification of my-restricted-dial-item-instance = vl-att3;

    att-values = call g2-get-attribute-values-of-item (my-restricted-dial-item-instance , sequence(the symbol my-att-dial, the symbol my-config-dial, the symbol my-specification));
    if (the value of the my-att-dial of att-values /= txt-att1) and  (the value of the my-att-dial of att-values = txt-tmp-att1) then begin
        call fail(node, "The text for the attribute my-att-dial is not as expected: expected [txt-att1] actual [the value of the my-att-dial of att-values]");
        return;
    end;

    if (the value of the my-config-dial of att-values /= txt-att2) and  (the value of the my-config-dial of att-values = txt-tmp-att2) then begin
        call fail(node, "The text for the attribute my-config-dial is not as expected: expected [txt-att2] actual [the value of the my-config-dial of att-values]");
        return;
    end; 

    spec-tmp = the value of the my-specification of att-values;

    if (the value of the value1 of spec-tmp[0] /= the value of the value1 of vl-att3[0]) then begin
        call fail(node, "The text for the attribute value1 of the value of the attribute my-spcification is wrong: expected [the value of the value1 of vl-att3[0]] got [the value of the value1 of spec-tmp[0]]");
        return;
    end;

    call pass(node);
end
