g2:c.PROCEDURE.g2:a.TEXT:
DEFAULT-ITEM-COLOR-TEST(node: class benchmark-node)
my-dial: class dial;
reg-symbol: symbol;
reg-length, i: integer;
color: symbol;

color1, color2, color3: symbol;
REG-I-LIGHT-SHADING: symbol = the symbol G2-ICON-LIGHT-SHADING;
REG-I-SPEC-REGION-3: symbol = the symbol G2-ICON-SPECIALTY-REGION-3;
REG-I-SPEC-REGION-2: symbol = the symbol G2-ICON-SPECIALTY-REGION-2;

err: class error;
err-hap: truth-value= false;
begin
    create a dial my-dial;

    {Scenario 1: invalid item, valid region. Object created on the test}
    begin
        color = call g2-get-default-item-color(the symbol fake, REG-I-SPEC-REGION-2);
    end on error(err)
        err-hap = true;
        delete err;
    end;
    if err-hap /= true then begin
        call fail(node, "Calling g2-get-default-item-color with unexistent item: an error should be raised");
        return;
    end;
    err-hap = false;

    {Scenario 2: valid item, invalid region. Object created on the test}
    begin
        color = call g2-get-default-item-color (my-dial, the symbol fake);
    end on error(err) 
        delete err;
        err-hap = false;
    end;
    if err-hap /= false then begin
        call fail(node, "Calling g2-get-default-item-color with unexistent region: an error should be reaised");
        return;
    end;

    {Scenario 3: invalid item, valid region. Local object to the workspace.}
    begin
        color = call g2-get-default-item-color(the symbol fake, REG-I-SPEC-REGION-3);
    end on error(err)
        err-hap = true;
        delete err;
    end;
    if err-hap /= true then begin
        call fail(node, "Calling g2-get-default-item-color with unexistent item: an error should be raised - Local object to the workspace");
        return;
    end;
    err-hap = false;

    {Scenario 4: valid item, invalid region. Local object to the workspace.}
    begin
        color = call g2-get-default-item-color (my-item-list , the symbol fake);
    end on error(err) 
        delete err;
        err-hap = false;
    end;
    if err-hap /= false then begin
        call fail(node, "Calling g2-get-default-item-color with unexistent region: an error should be reaised - Local object to the workspace");
        return;
    end;   

    {Scenario 5: different regions of the same item, having both different and same colors}
    color1 = call g2-get-default-item-color (my-item-list, REG-I-LIGHT-SHADING);
    color2 = call g2-get-default-item-color (my-item-list, REG-I-SPEC-REGION-2); 
    color3 = call g2-get-default-item-color (my-item-list, REG-I-SPEC-REGION-3); 
    if color1 = color2 then begin
        call fail(node, "Region [loc1] have the same color of region [REG-I-SPEC-REGION-2], but they were sopposed to have different ones");
        return;
    end;
    if color2 /= color3 then begin
        call fail(node, "Region [REG-I-SPEC-REGION-2] have a different color of than region [REG-I-SPEC-REGION-3], but they were sopposed to have the same");
        return;
    end;

    color1 = call g2-get-default-item-color (my-dial, REG-I-SPEC-REGION-2); 
    color2 = call g2-get-default-item-color (my-dial, REG-I-SPEC-REGION-3); 
    if color1 = color2 then begin
        call fail(node, "Region [REG-I-SPEC-REGION-2] has the same color of region [REG-I-SPEC-REGION-3]. They were supposed to have different colors");
        return;
    end;

    delete my-dial;
    call pass(node);
end

DEFAULT-ITEM-COLOR-PATTERN-TEST(node: class benchmark-node)
my-obj-twopat: class my-cls-twopat ;
my-dial: class dial; 
clr-pattern: class symbol-array;
i, reg-length: integer;

icon-background : symbol = the symbol g2-icon-background;
light-gray: symbol = the symbol light-gray;
i-light-shading: symbol = the symbol g2-icon-light-shading;
s-smoking: symbol = the symbol smoke;

err: class error;
err-hap: truth-value = false;
begin
    create a my-cls-twopat my-obj-twopat;
    create a dial my-dial;

    {Scenario 1: invalid item}
    begin
        clr-pattern = call g2-get-default-item-color-pattern(the symbol fake);
    end on error(err)
        delete err;
        err-hap = true;
    end;
    if err-hap = false then begin
        call fail(node, "Calling g2-get-default-item-color-pattern with a nonexistent item. An error should be reaised.");
        return;
    end;

    {Scenario 2: item with only one color pattern}
    clr-pattern = call g2-get-default-item-color-pattern (my-obj-onepat);
    reg-length = the array-length of clr-pattern; 
    if (reg-length /= 2) or (clr-pattern[0] /= i-light-shading ) or (clr-pattern[1] /= s-smoking) then begin
        call fail(node, "Calling  g2-get-default-item-color-pattern for my-obj-onepat should return 2 elements (one collor pattern), but it returned [reg-length]. The collor pattern should be [i-light-shading]: [s-smoking], but returned [clr-pattern[0]]: [clr-pattern[1]]");
        return;
    end;

    {Scenario 3: item with two color patterns}
    clr-pattern = call g2-get-default-item-color-pattern (my-obj-twopat);
    reg-length = the array-length of clr-pattern; 
    if (reg-length /= 4) or (clr-pattern[0] /= icon-background) or (clr-pattern[1] /= light-gray) then begin
        call fail(node, "Calling  g2-get-default-item-color-pattern for my-obj-twopat should return 4 elements (two collor pattern), but it returned [reg-length]. The collor pattern should be [icon-background]: [light-gray], but returned [clr-pattern[0]]: [clr-pattern[1]] ");
        return;
    end;

    {Scenario 4: item with several color patterns}
    clr-pattern = call g2-get-default-item-color-pattern (my-item-list);
    reg-length = the array-length of clr-pattern;
    if (reg-length <= 4) then begin
        call fail(node, "Calling g2-get-default-item-color-pattern for an item-list with more than 2 color patterns. Expected more than two color patterns (4 elements) but got [reg-length] elements");
        return;
    end;

    {test for the item created on this procedure}
    clr-pattern = call g2-get-default-item-color-pattern (my-dial);
    reg-length = the array-length of clr-pattern;
    if (reg-length <= 4) then begin
        call fail(node, "Calling g2-get-default-item-color-pattern for a dial with more than 2 color patterns. Expected more than two color patterns (more than 4 elements) but got [reg-length] elements");
        return;
    end;


    delete my-dial;
    delete my-obj-twopat;
    call pass(node);
end

ITEM-COLOR-REGIONS-TEST(node : class benchmark-node)
my-obj-tworegs: class my-cls-twopat ; 
my-dial: class dial; 
regions: class symbol-array;

smbl-light-shading: symbol = THE SYMBOL G2-ICON-LIGHT-SHADING;
smbl-icon-background: symbol = THE SYMBOL G2-ICON-BACKGROUND;

element: symbol;
length, idx: integer;

err: class error;
err-hap: truth-value = false;
begin
    create a my-cls-twopat my-obj-tworegs;    
    create a dial my-dial; 

    {Scenario 1: Invalid item}
    begin
        regions = call g2-get-item-color-regions (the symbol fake);
    end on error(err) 
        err-hap = true;
        delete err;
    end;
    if err-hap = false then begin
        call fail(node, "Calling g2-get-item-color-regions  with an invalid item should raise an error");
        return;
    end;

    {Scenario 2: Item with one region}
    regions = call g2-get-item-color-regions(my-obj-onepat );
    length = the array-length of regions;
    if length /= 1 or regions[0] /= smbl-light-shading then begin
        call fail(node, "Calling g2-get-item-color-regions on an one region item should return only one region but returned [length] and the region should be G2-ICON-LIGHT-SHADING but returned [regions[0]]");
        return;
    end;

    {Scenario 3: item with two regions}
    regions = call g2-get-item-color-regions (my-obj-tworegs);
    length = the array-length of regions;
    if (length /= 2) or regions[0] /= smbl-icon-background  or regions[1] /= smbl-light-shading then begin
        call fail(node, "Calling g2-get-item-color-regions on a two region item should return two regions but returned [length]. The regions should be G2-ICON-BACKGROUND and G2-ICON-LIGHT-SHADING, but returned [regions[0]] and [regions[1]]");
        return;
    end;

    {Scenario 4: item with several regions}
    regions = call g2-get-item-color-regions (my-item-list);
    length = the array-length of regions;
    if length <= 2 then begin
        call fail(node, "Expected more than two regions for the item, but got [length]");
        return;
    end;

    regions = call g2-get-item-color-regions (my-dial);
    length = the array-length of regions;
    if length <= 2 then begin
        call fail(node, "Calling g2-get-item-color-regions on a dynamically created item. Expected more than two regions for the item, but got [length]");
        return;
    end;    

    delete my-dial;
    delete my-obj-tworegs; 
   call pass(node);
end

SET-ITEM-COLOR-TEST(node: class benchmark-node)
color-obj: class my-color-class;

f-circle: symbol = the symbol region-filled-circle;
circle: symbol = the symbol region-circle;
color-blue: symbol = the symbol blue;
color-l-blue: symbol = the symbol light-blue;

color-salmon: symbol = the symbol salmon;
color-tan: symbol = the symbol tan;

icon-background : symbol = the symbol g2-icon-background; 
C: symbol;

err: class error;
err-hap: truth-value;
ret-comparison: symbol;
begin
    create a my-color-class color-obj;
    transfer color-obj to this workspace at (1, -20);

    {Scenario 1: Invalid item}
    begin
     call g2-set-item-color(the symbol fake, f-circle, color-blue);
    end on error(err)
        delete err;
        err-hap = true;
    end;
    if err-hap /= true then begin
        call fail(node, "Calling g2-set-item-color with an invalid item should raise an error.");
        return;
    end;   

    {Scenario 2: Valid item, region not belonging to said item}
    err-hap = false;
    begin
        call g2-set-item-color(color-obj, icon-background, color-blue); 
    end on error(err)
        delete err;
        err-hap = true;
    end;
    if err-hap /= true then begin
        call fail(node, "Calling g2-set-item-color with a region that doesn't belong to the item should raise an error");
        return;
    end;

    {Scenario 3: valid item, valid region, a few different colors}
    wait for 2 seconds;
    ret-comparison = call change-color-helper (color-obj, f-circle, color-blue);
    if ret-comparison /= the symbol none then begin
        call fail(node, "Geting the color for the region [f-circle] should return [color-blue] but returned [ret-comparison]");
        return;
    end;

    wait for 2 seconds;
    ret-comparison = call change-color-helper (color-obj, f-circle, color-l-blue); 
    if ret-comparison /= the symbol none then begin
        call fail(node, "Geting the color for the region [f-circle] should return [color-l-blue] but returned [ret-comparison]");
        return;
    end;

    wait for 2 seconds;
    ret-comparison = call change-color-helper (my-color-instance , f-circle, color-salmon); 
    if ret-comparison /= the symbol none then begin
        call fail(node, "Geting the color for the region [f-circle] should return [color-salmon] but returned [ret-comparison]");
        return;
    end; 

    wait for 2 seconds;
    ret-comparison = call change-color-helper (my-color-instance , f-circle, color-tan); 
    if ret-comparison /= the symbol none then begin
        call fail(node, "Geting the color for the region [f-circle] should return [color-tan] but returned [ret-comparison]");
        return;
    end; 
    call pass(node);
end

change-color-helper(item: class item, region: symbol, color: symbol) = (symbol)
C: symbol;
begin
    call g2-set-item-color(item, region, color);
    C = call g2-get-item-color(item, region);
    if C /= color then 
        return C;
    return the symbol none;
end
