g2:c.PROCEDURE.g2:a.TEXT:
message-handler(msg-content: text)

begin
    // post "receive message [msg-content]";
    conclude that message-posted-count = message-posted-count + 1;
end

sockman-hide-error-test(node: class benchmark-node)
    s: symbol;
    t: text;
begin
    conclude that message-posted-count = 0;
    wait for 1 seconds;
    // register handler of messages
    call g2-register-message-board-message-handler(message-handler);
    call _g2-java-create-socket(client-socket-1, the g2-java-socket-manager-interface connected to client-socket-1);
    // check point #1: error shown
    wait for 1 seconds;
    if message-posted-count /= 1 then begin
        call g2-deregister-message-board-message-handler();
        call fail(node, "error message should be posted but not posted");
        return;
    end;
    conclude that _sockman-hide-errors = true;
    call _g2-java-create-socket(client-socket-1, the g2-java-socket-manager-interface connected to client-socket-1);
    // check point #2: error should not shown
    wait for 1 seconds;
    if message-posted-count > 1 then begin
        call g2-deregister-message-board-message-handler();
        call fail(node, "error message should not be posted but posted");
        return;
    end;
    call g2-deregister-message-board-message-handler();
    call pass(node);
end on error(s, t)
    call g2-deregister-message-board-message-handler();
    call fail(node, t);
end

two-connection-test(node: class benchmark-test)
    
    s: symbol;
    t: text;
    wait-seconds: integer;
    ret: integer;

begin
    // start sockman bridge
    call start-sockman();
    wait for 30 seconds;
    
    // verify local sockman interface is connected
    change the text of the gsi-connection-configuration of g2-java-example-interface to "tcp-ip host @"localhost@" port-number 22044";
    wait for 2 seconds;
    if the gsi-interface-status of g2-java-example-interface /= 2 then begin
        call shutdown-dependency();
        call fail(node, "sockman bridge is not started");
        return;
    end;
    
    // start remote g2
    call start-slave-g2();
    wait for 20 seconds;
    change the text of the icp-connection-specification of remote-g2-server to "tcp-ip host @"localhost@" port-number 11111";
    wait for 2 seconds;
    if the interface-status of remote-g2-server is inactive then begin
        call shutdown-dependency();
        call fail(node, "remote g2 server not started");
        return;
    end;
    
    // check remote sockman interface status
    ret = call get-interface-status-rmt() across remote-g2-server;
    if ret /= 2 then begin
        call shutdown-dependency();
        call fail(node, "remote interface not connected");
        return;
    end;
    
    // start local server
    call _g2-java-create-socket(server-socket-1, the g2-java-socket-manager-interface connected to server-socket-1);
    wait for 2 seconds;
    if the status of server-socket-1 is inactive then begin
        call shutdown-dependency();
        call fail(node, "server client not active");
        return;
    end;
    
    // start remote client
    ret = call start-client-rmt() across remote-g2-server;
    if ret /= 1 then begin
        call shutdown-dependency();
        call fail(node, "remote client not active, error code = [ret]");
        return;
    end;
    
    // start local client
    call _g2-java-create-socket(client-socket-1, the g2-java-socket-manager-interface connected to client-socket-1);
    wait for 2 seconds;
    if the status of client-socket-1 is inactive then begin
        call shutdown-dependency();
        call fail(node, "local client not active");
        return;
    end;
    
    // close server socket
    call g2-java-socket-manager-close-socket (the g2-java-socket-manager-interface connected to server-socket-1, server-socket-1);
    wait for 2 seconds;
    
    // check remote client status
    s = call get-client-status-rmt() across remote-g2-server;
    if s is active then begin
        call shutdown-dependency();
        call fail(node, "remote client not active");
        return;
    end;
    
    // check local client status
    if the status of client-socket-1 is active then begin
        call shutdown-dependency();
        call fail(node, "local client not active");
        return;
    end;
    
    call shutdown-dependency();
    call pass(node);
end on error(s, t)
    call shutdown-dependency();
    call fail(node, t);
end

shutdown-dependency()

begin

    call stop-sockman();
    call stop-slave-g2();

end

check-connection-test(node: class benchmark-node)

    s: symbol;
    t: text;

begin
    // start sockman bridge
    call start-sockman();
    wait for 30 seconds;
    
    // verify local sockman interface is connected
    change the text of the gsi-connection-configuration of g2-java-example-interface to "tcp-ip host @"localhost@" port-number 22044";
    wait for 2 seconds;
    if the gsi-interface-status of g2-java-example-interface /= 2 then begin
        call shutdown-dependency();
        call fail(node, "sockman bridge is not started");
        return;
    end; 

    // start local server
    conclude that the enable-keep-alive of server-socket-1 = true;
    wait for 1 seconds;
    call _g2-java-create-socket(server-socket-1, the g2-java-socket-manager-interface connected to server-socket-1);
    wait for 2 seconds;
    if the status of server-socket-1 is inactive then begin
        call shutdown-dependency();
        call fail(node, "server client not active");
        return;
    end; 

    // start local client
    conclude that the enable-keep-alive of client-socket-1 = true;
    wait for 1 seconds;
    call _g2-java-create-socket(client-socket-1, the g2-java-socket-manager-interface connected to client-socket-1);
    wait for 2 seconds;
    if the status of client-socket-1 is inactive then begin
        call shutdown-dependency();
        call fail(node, "local client not active");
        return;
    end; 

    s = call g2-java-socket-check-connection(client-socket-1, "check-status-syn", "check-status-ack", 10);
    
    if s /= the symbol disconnected then begin
        call shutdown-dependency();
        call fail(node, "status should be disconnected when no ack message received");
        return;
    end;

    start delay-send-ack-message();
    s = call g2-java-socket-check-connection(client-socket-1, "check-status-syn", "check-status-ack", 10); 

    if s /= the symbol connected then begin
        call shutdown-dependency();
        call fail(node, "status should be connected when ack message received");
        return;
    end;

    start g2-java-socket-check-connection(client-socket-1, "check-status-syn", "check-status-ack", 10); 
    wait for 1 seconds;
    s = call g2-java-socket-check-connection(client-socket-1, "check-status-syn", "check-status-ack", 10); 

    if s /= the symbol in-progress then begin
        call shutdown-dependency();
        call fail(node, "status should be in-progress when continuously call check-connection procedure");
        return;
    end;

    call shutdown-dependency();
    call pass(node);
end on error(s, t)
    call shutdown-dependency();
    call fail(node, t);
end

delay-send-ack-message()

begin

    wait for 5 seconds;
    call _g2-java-socket-send-message-over-socket(server-socket-1, "check-status-ack", g2-java-example-interface);

end

set-up-script-commands()

    installation-dir, dir-sep, file-path, src-folder: text;
    version-struct: structure;
    
begin

    version-struct = call g2-get-g2-version-information();
    installation-dir = call grtl-get-installation-directory();
    dir-sep = call grtl-get-file-system-delimiter();
    src-folder = "[installation-dir][dir-sep]..[dir-sep]..[dir-sep]..[dir-sep]";
    // src-folder = "[installation-dir][dir-sep]src[dir-sep]";

    if the enterprise of version-struct then begin
        conclude that start-sockman-command = "[src-folder]tests[dir-sep]test-sockman[dir-sep]start-sockman.bat ENT";
    end else begin
        conclude that start-sockman-command = "[src-folder]tests[dir-sep]test-sockman[dir-sep]start-sockman.bat STD";
    end;
    conclude that stop-sockman-command = "[src-folder]tests[dir-sep]test-sockman[dir-sep]start-sockman.bat STOP";
    conclude that start-slave-g2-command = "[src-folder]scripts[dir-sep]nt[dir-sep]StartServer.bat -tcpport 11111 -kb [src-folder]tests[dir-sep]test-sockman[dir-sep]sockman-tests-slave.xml";
end

stop-sockman()

    status: truth-value;

begin
    if sockman-proc-id > 0 then begin
        call g2-spawn-process-to-run-command-line(stop-sockman-command);
        wait for 1 seconds;
        status = call g2-kill-process(sockman-proc-id);
        conclude that sockman-proc-id = -1.0;
    end;
end

start-sockman()

    proc-id: float;

begin
    
    proc-id = call g2-spawn-process-to-run-command-line(start-sockman-command);
    conclude that sockman-proc-id = proc-id;
   
end

stop-slave-g2()

    status: truth-value;

begin
    if slave-g2-proc-id > 0 then begin
        status = call g2-kill-process(slave-g2-proc-id);
        conclude that slave-g2-proc-id = -1.0;
    end;
end

start-slave-g2()

    proc-id: float;

begin
    
    proc-id = call g2-spawn-process-to-run-command-line(start-slave-g2-command);
    conclude that slave-g2-proc-id = proc-id;
   
end
